import React, { Component } from "react";
let array = [];

class OrderInformation extends Component {
  state = {
    books: [],
    created: ""
  };
  componentDidMount() {
    fetch(`https://a-novel-idea.herokuapp.com/orders/${this.props.match.params.id}`)
      .then(resp => resp.json())
      .then(data => {
        console.log(data);
        this.setState({
          books: data.books,
          created: data.created_at
        });
      });
  }

  renderBooks = () =>
    this.state.books.map(book => (
      <div className="innerCard">
        <img className="book-img" src={book.image} alt={book.title} />
        <h3 className="bookTitleLink"> {book.title} </h3>
        <h5 className="bookAuthorLink"> {book.author} </h5>
        <p className="bookPriceLink"> £{book.price} </p>
      </div>
    ));

  priceArray = () => {
    array = this.state.books.map(book => book.price);
    // debugger;
    return array.reduce((a, b) => a + b, 0);
  };
  //This doesn't work, it needs to be: array[0].reduce((a, b) => a + b, 0)
  //On loading it says array is empty.

  // totalOrderCost = () => this.priceArray().reduce((a, b) => a + b, 0);

  getDateString = () => {
    let orderDate = new Date(this.state.created);
    const days = [
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday",
      "Sunday"
    ];
    const months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December"
    ];
    let weekday = days[orderDate.getDay()];
    let month = months[orderDate.getMonth()];
    return `${weekday}, ${orderDate.toDateString()}`;
  };

  render() {
    return (
      <>
        <h1>Order Number {this.props.match.params.id}</h1>
        <h3>Ordered on {this.getDateString()}s</h3>
        <h3>Total Cost of £{this.priceArray()}</h3>
        <div className="Card">{this.renderBooks()}</div>
      </>
    );
  }
}

export default OrderInformation;
