import React, { Component } from "react";

const SERVERURL = `https://a-novel-idea.herokuapp.com/orders`;

class Orders extends Component {
  state = {
    orders: []
  };

  componentDidMount() {
    fetch(SERVERURL)
      .then(resp => resp.json())
      .then(data =>
        this.setState({
          orders: data
        })
      );
  }

  render() {
    return (
      <div>
        {this.state.orders.map(order => (
          <p>Total: Â£{order.total}</p>
        ))}
      </div>
    );
  }
}

export default Orders;
