import React, { Component } from "react";
import { Link } from "react-router-dom";

const BASEURL = "https://www.googleapis.com/books/v1/volumes";

class Books extends Component {
  state = {
    books: []
  };

  componentDidMount() {
    fetch(
      BASEURL +
        '?q=""&printType=books&orderBy=newest&maxResults=40&langRestrict="en"'
    )
      .then(resp => resp.json())
      .then(data => this.setState({ books: data.items }));
  }

  renderBooks = () => {
    return this.state.books.map(book => <Books book={book} key={book.id} />);
  };

  render() {
    return (
      <div className="innerCard">
        <Link to={`/books/${this.props.book.id}`}>
          <h3> {this.props.book.volumeInfo.title} </h3>
          <img
            className="book-img"
            src={this.props.book.volumeInfo.imageLinks.thumbnail}
            alt={this.props.book.volumeInfo.title}
          />
        </Link>
      </div>
    );
  }
}

export default Books;
