import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import BookBarFiction from "./BookBarFiction";
import Slider from "./Slider";

class Home extends Component {
  state = {
    redirect: false
  };
  //////////////////////////////
  //SLIDER//
  //////////////////////////////
  handleSliderClick = () => {
    this.setState({
      redirect: !this.state.redirect
    });
  };

  renderRedirect = () => {
    if (this.state.redirect) {
      return <Redirect to="/books" />;
    }
  };
  //////////////////////////////
  //BOOKBAR//
  //////////////////////////////
  moreSushi = () => {
    if (this.state.index === this.state.allTheSushi.length - 4) {
      this.setState({
        index: 0
      });
    } else {
      this.setState({
        index: this.state.index + 4
      });
    }
  };
  //////////////////////////////
  //RENDER//
  //////////////////////////////
  render() {
    return (
      <div>
        <Slider handleSliderClick={this.handleSliderClick} />
        <BookBarFiction />
        {this.renderRedirect()}
      </div>
    );
  }
}

export default Home;
