import React, { Component } from "react";
import { Link } from "react-router-dom";

const load = key => JSON.parse(localStorage.getItem(key));

load("basket");

class Cart extends Component {
  state = {
    basket: load("basket")
  };

  renderBooks = () => {
    this.state.basket.map(book => renderPage(book));
  };

  renderPage = book => {
    return (
      <div className="innerCard">
        <Link style={{ textDecoration: "none" }} to={`/books/${bookNum.id}`}>
          <img className="book-img" src={bookNum.image} alt={bookNum.title} />
          <h3 className="bookTitleLink"> {bookNum.title} </h3>
          <h5 className="bookAuthorLink"> {bookNum.author} </h5>
          <p className="bookPriceLink"> £{bookNum.price} </p>
        </Link>
        <button
          value={bookNum.id}
          onClick={() => this.handleClick(name)}
          // onClick={() => console.log(name)}
          className="WishRemove"
        >
          Remove
        </button>
      </div>
    );
  };

  // handleClick = name => {
  //   localStorage.removeItem(name);
  // };

  render() {
    return <h1>hey</h1>;
  }
}

export default Cart;

