import React, { Component } from "react";
import { Link } from "react-router-dom";

class Cart extends Component {
  state = {
    clicked: false
  };

  renderBooks = () => {
    return this.props.cart.cart_books.map(cartBook => (
      <div className="innerCard">
        <Link style={{ textDecoration: "none" }} to={`/books/${book.id}`}>
          <img
            className="book-img"
            src={cartBook.book.image}
            alt={cartBook.book.title}
          />
          <h3 className="bookTitleLink"> {cartBook.book.title} </h3>
          <h5 className="bookAuthorLink"> {cartBook.book.author} </h5>
          <p className="bookPriceLink"> Â£{cartBook.book.price} </p>
        </Link>
        <button onClick={() => this.handleClick(this.props.user.cart.id)}>
          Remove
        </button>
      </div>
    ));
  };

  handleClick = cartId => {
    this.setState({
      clicked: !this.state.clicked
    });
    console.log(this.props.cart);
    // this.deleteBookFromServer(cartId);
  };

  deleteBookFromServer = cartId => {
    return fetch(`http://localhost:3000/cart_books/${cartId}`, {
      method: "DELETE"
    })
      .then(respo => respo.json())
      .then(console.log);
  };

  render() {
    return (
      <>
        <h1>{this.props.user.first_name}'s Cart</h1>
        <div className="Card">{this.renderBooks}</div>
      </>
    );
  }
}

export default Cart;
