import React, { Component } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import NavBar from "../components/NavBar";
import Footer from "../components/Footer";
import Home from "../components/Home";
// import Books from "../components/Books";
import Orders from "../components/Orders";
import Wishlists from "../components/Wishlists";
import Books from "../components/Books";
import Genre from "../components/Genre";
import BookInformation from "../components/BookInformation";
import { saveGenre, getGenres } from "../components/Filter";

const BASEURL = "https://www.googleapis.com/books/v1/volumes";

class App extends Component {
  state = {
    books: []
  };

  componentDidMount() {
    fetch(BASEURL + '?q=""&printType=books&orderBy=newest&langRestrict="en"')
      .then(resp => resp.json())
      .then(data => this.setState({ books: data.items }));
  }

  render() {
    const filterBooks = saveGenre(this.state.books);
    console.log(getGenres(this.state.books));

    return (
      <Router>
        <div className="App">
          <NavBar />
          <Switch>
            <Route path="/" exact render={() => <Home books={filterBooks} />} />
            <Route
              path="/books"
              exact
              render={() => <Books books={this.state.books} />}
            />
            <Route path="/books/:id" exact component={BookInformation} />
            <Route path="/orders" exact component={Orders} />
            <Route path="/wishlists" exact component={Wishlists} />
          </Switch>
          <Footer />
        </div>
      </Router>
    );
  }
}
export default App;
