import React, { Component } from "react";
import { Link } from "react-router-dom";

class Wishlists extends Component {
  state = {
    books: {
      book1: {},
      book2: {},
      book3: {},
      book4: {},
      book5: {},
      book6: {},
      book7: {},
      book8: {},
      book9: {},
      book10: {}
    }
  };

  componentDidMount() {
    this.hydrateStateWithLocalStorage();
  }

  renderBooks = () => {
    return (
      <div className="innerCard">
        <Link
          style={{ textDecoration: "none" }}
          to={`/books/${this.state.books.book1.id}`}
        >
          <img
            className="book-img"
            src={this.state.books.book1.image}
            alt={this.state.books.book1.title}
          />
          <h3 className="bookTitleLink"> {this.state.books.book1.title} </h3>
          <h5 className="bookAuthorLink"> {this.state.books.book1.author} </h5>
          <p className="bookPriceLink"> Â£{this.state.books.book1.price} </p>
        </Link>
      </div>
    );
  };

  hydrateStateWithLocalStorage() {
    for (let key in this.state.books) {
      if (localStorage.hasOwnProperty(key)) {
        let value = localStorage.getItem(key);
        try {
          value = JSON.parse(value);
          this.setState({ books: { [key]: value } });
        } catch (e) {
          this.setState({ [key]: value });
        }
      }
    }
  }

  render() {
    return <div>{this.renderBooks()}</div>;
  }
}

export default Wishlists;
