  //     return (
  //       <>
  //         <Link to="/wishlists" style={{ textDecoration: "none" }}>
  //           <button> View WishList </button>{" "}
  //         </Link>
  //       </>
  //     );
  //   }
  // };

  handleCartClick = e => {
    this.setState({
      clicked: true
    });
    console.log("Clicked");

    // const cartbook = {
    //   cart_id: 1,
    //   book_id: this.state.book.id
    // };

    e.preventDefault();

    this.addBookToServer(this.setBookDetails());
    // this.addBookToCartOnServer(cartbook);
  };

  addBookToServer = book => {
    return fetch(`http://localhost:3000/books`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(book)
    })
      .then(resp => resp.json())
      .then(console.log);
  };

  // addBookToCartOnServer = cartbook => {
  //   return fetch(`http://localhost:3000/cart_books`, {
  //     method: "POST",
  //     headers: {
  //       "Content-Type": "application/json"
  //     },
  //     body: JSON.stringify(cartbook)
  //   });
  // };

  // handleWishClick = e => {
  //   console.log("Clicked");
  //   e.preventDefault();
  //   this.addBookToServerWishlist(this.setBookDetails());
  // };
