import React, { Component } from "react";
import { Link } from "react-router-dom";

class Cart extends Component {
  state = {
    books: {
      book1: {},
      book2: {},
      book3: {},
      book4: {},
      book5: {},
      book6: {},
      book7: {},
      book8: {},
      book9: {},
      book10: {}
    },
    clicked: false
  };

  book1 = () => this.state.book1;
  book2 = () => this.state.book2;
  book3 = () => this.state.book3;
  book4 = () => this.state.book4;
  book5 = () => this.state.book5;
  book6 = () => this.state.book6;
  book7 = () => this.state.book7;
  book8 = () => this.state.book8;
  book9 = () => this.state.book9;
  book10 = () => this.state.book10;

  componentDidMount() {
    for (let key in this.state.books) {
      if (localStorage.hasOwnProperty(key)) {
        let value = localStorage.getItem(key);
        try {
          value = JSON.parse(value);
          this.setState({ [key]: value });
        } catch (e) {
          this.setState({ [key]: value });
        }
      }
    }
  }

  renderBooks = (bookNum, name) => {
    if (bookNum) {
      return (
        <div className="innerCard">
          <Link style={{ textDecoration: "none" }} to={`/books/${bookNum.id}`}>
            <img className="book-img" src={bookNum.image} alt={bookNum.title} />
            <h3 className="bookTitleLink"> {bookNum.title} </h3>
            <h5 className="bookAuthorLink"> {bookNum.author} </h5>
            <p className="bookPriceLink"> Â£{bookNum.price} </p>
          </Link>
          <button
            value={bookNum.id}
            onClick={() => this.handleClick(name)}
            // onClick={() => console.log(name)}
            className="WishRemove"
          >
            Remove
          </button>
        </div>
      );
    }
  };

  handleClick = name => {
    localStorage.removeItem(name);
  };

  renderPage = () => {
    if (
      this.state.book1 ||
      this.state.book2 ||
      this.state.book3 ||
      this.state.book4 ||
      this.state.book5 ||
      this.state.book6 ||
      this.state.book7 ||
      this.state.book8 ||
      this.state.book9 ||
      this.state.book10
    ) {
      return (
        <div className="Card">
          {this.renderBooks(this.book1(), "book1")}
          {this.renderBooks(this.book2(), "book2")}
          {this.renderBooks(this.book3(), "book3")}
          {this.renderBooks(this.book4(), "book4")}
          {this.renderBooks(this.book5(), "book5")}
          {this.renderBooks(this.book6(), "book6")}
          {this.renderBooks(this.book7(), "book7")}
          {this.renderBooks(this.book8(), "book8")}
          {this.renderBooks(this.book9(), "book9")}
          {this.renderBooks(this.book10(), "book10")}
        </div>
      );
    } else {
      return <h1>Your List is Empty...</h1>;
    }
  };

  render() {
    return this.renderPage();
  }
}

export default Cart;
