import React, { Component } from "react";

class Filter extends Component {
  state = {
    filtered: []
  };
  saveGenre = () => {
    this.setState({
      filtered: this.props.books.map(book =>
        book.volumeInfo.categories.filter(genre => genre === "Fiction")
      )
    });
  };

  render() {
    return <div />;
  }
}

export default Filter;
