import React, { Component } from "react";
import { Link } from "react-router-dom";

class Wishlists extends Component {
  state = {
    books: {
      book1: {},
      book2: {},
      book3: {},
      book4: {},
      book5: {},
      book6: {},
      book7: {},
      book8: {},
      book9: {},
      book10: {}
    }
  };

  tryingSomething = () => this.state.book1;

  componentDidMount() {
    for (let key in this.state.books) {
      if (localStorage.hasOwnProperty(key)) {
        let value = localStorage.getItem(key);
        try {
          value = JSON.parse(value);
          this.setState({ [key]: value });
        } catch (e) {
          this.setState({ [key]: value });
        }
      }
    }
  }

  renderBooks = () => {
    if (this.tryingSomething()) {
      return (
        <div className="innerCard">
          <Link
            style={{ textDecoration: "none" }}
            to={`/books/${this.tryingSomething().id}`}
          >
            <img
              className="book-img"
              src={this.tryingSomething().image}
              alt={this.tryingSomething().title}
            />
            <h3 className="bookTitleLink"> {this.tryingSomething().title} </h3>
            <h5 className="bookAuthorLink">
              {" "}
              {this.tryingSomething().author}{" "}
            </h5>
            <p className="bookPriceLink"> Â£{this.tryingSomething().price} </p>
          </Link>
        </div>
      );
    }
  };

  render() {
    return <div>{this.renderBooks()}</div>;
  }
}

export default Wishlists;
