import React, { Component } from "react";

const SERVERURL = `http://localhost:3000/orders`;

class Orders extends Component {
  state = {
    book: [],
    image: {}
  };

  componentDidMount() {
    fetch(SERVERURL + `/${this.props.match.params.id}`)
      .then(resp => resp.json())
      .then(data =>
        this.setState({
          book: data.volumeInfo,
          image: data.volumeInfo.imageLinks
        })
      );
  }

  render() {
    return (
      <div>
        <p>hello</p>
      </div>
    );
  }
}

export default Orders;
