const books = [
  "book1",
  "book2",
  "book3",
  "book4",
  "book5",
  "book6",
  "book7",
  "book8",
  "book9",
  "book10"
];

class Wishlists extends Component {
  renderBooks = () => {
    let x = "something";
    for (let key of books) {
      if (localStorage.hasOwnProperty(key)) {
        let value = localStorage.getItem(key);
        x = (
          <div>
            <h3> {JSON.parse(value).title} </h3>
          </div>
        );
      }
    }
    return x;
  };

  render() {
    return <div>{this.renderBooks()}</div>;
  }
}

export default Wishlists;

