import React, { Component } from "react";

class Filter extends Component {
  state = {
    filtered: ""
  };

  componentDidMount() {
    this.saveGenre();
  }
  saveGenre = () => {
    this.setState({
      filtered: [
        ...this.state.filtered,
        this.props.books.filter(book =>
          book.volumeInfo.categories.includes("Biography & Autobiography")
        )
      ]
    });
  };

  render() {
    return (
      <div>
        <h1>filter</h1>
      </div>
    );
  }
}

export default Filter;
