import React, { Component } from "react";
import { Link } from "react-router-dom";

class Wishlists extends Component {
  state = {
    book1: {},
    book2: {},
    book3: {},
    book4: {},
    book5: {},
    book6: {},
    book7: {},
    book8: {},
    book9: {},
    book10: {}
  };

  componentDidMount() {
    this.hydrateStateWithLocalStorage();
  }

  renderBooks = () => {
    return (
      <div className="innerCard">
        <Link style={{ textDecoration: "none" }} to={`/books/${book.id}`}>
          <img
            className="book-img"
            src={this.state.book1.volumeInfo.imageLinks.thumbnail}
            alt={this.state.book1.volumeInfo.title}
          />
          <h3 className="bookTitleLink">
            {" "}
            {this.state.book1.volumeInfo.title}{" "}
          </h3>
          <h5 className="bookAuthorLink">
            {" "}
            {this.state.book1.volumeInfo.authors}{" "}
          </h5>
          <p className="bookPriceLink">
            {" "}
            Â£{this.state.book1.saleInfo.listPrice.amount}{" "}
          </p>
        </Link>
      </div>
    );
  };

  hydrateStateWithLocalStorage() {
    for (let key in this.state) {
      if (localStorage.hasOwnProperty(key)) {
        let value = localStorage.getItem(key);
        try {
          value = JSON.parse(value);
          this.setState({ [key]: value });
        } catch (e) {
          this.setState({ [key]: value });
        }
      }
    }
  }

  render() {
    return <div>{this.renderBooks()}</div>;
  }
}

export default Wishlists;
