import React from "react";

const Slide = ({ image }, props) => {
  const styles = {
    backgroundImage: `url(${image})`,
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    backgroundPosition: "50% 60%"
  };
  return (
    <div
      onClick={this.props.handleSliderClick}
      className="slide"
      style={styles}
    />
  );
};
export default Slide;
