import React, { Component } from "react";
import { Link } from "react-router-dom";

const BASEURL = "https://www.googleapis.com/books/v1/volumes";

class BookInformation extends Component {
  state = {
    book: []
  };

  componentDidMount() {
    fetch(BASEURL + `/${this.props.match.params.id}`)
      .then(resp => resp.json())
      .then(data => this.setState({ book: data }));
  }

  render() {
    return (
      <div className="innerCard">
        <h1>Title: {this.state.book.title}</h1>
        <h2>Author: {this.state.book.authors[0]}</h2>
        <h3>Publisher: {this.state.book.publisher}</h3>
        <h4>Published Date: {this.state.book.publishedDate}</h4>
        <h5>Info: {this.state.book.snippet}</h5>
        <h6>Page Count: {this.state.book.pageCount}</h6>
        <h6>Page Count: {this.state.book.categories[0]}</h6>
        <p>Description: {this.state.book.description}</p>
        <img
          src={this.state.book.imageLinks.thumbnail}
          alt={this.state.book.title}
        />
      </div>
    );
  }
}

export default BookInformation;
