import React, { Component } from "react";
import { Link } from "react-router-dom";

class Wishlists extends Component {
  state = {
    books: {
      book1: {},
      book2: {},
      book3: {},
      book4: {},
      book5: {},
      book6: {},
      book7: {},
      book8: {},
      book9: {},
      book10: {}
    }
  };

  tryingSomething = () => this.state.book1;

  componentDidMount() {
    for (let key in this.state.books) {
      if (localStorage.hasOwnProperty(key)) {
        let value = localStorage.getItem(key);
        try {
          value = JSON.parse(value);
          this.setState({ [key]: value });
        } catch (e) {
          this.setState({ [key]: value });
        }
      }
    }
  }

  renderBooks = example => {
    if (example) {
      return (
        <div className="innerCard">
          <Link style={{ textDecoration: "none" }} to={`/books/${example.id}`}>
            <img className="book-img" src={example.image} alt={example.title} />
            <h3 className="bookTitleLink"> {example.title} </h3>
            <h5 className="bookAuthorLink"> {example.author} </h5>
            <p className="bookPriceLink"> Â£{example.price} </p>
          </Link>
        </div>
      );
    }
  };

  render() {
    return <div>{this.renderBooks(this.tryingSomething)}</div>;
  }
}

export default Wishlists;
