import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import BookBar from "./BookBar";
import Slider from "./Slider";

class Home extends Component {
  state = {
    redirect: false,
    index: 0,
    fictionBooks: [],
    juvenileFiction: [],
    education: []
  };

  componentDidMount() {
    this.fetchFilteredBooksForBookbarFromServer("Fiction").then(
      this.addVerifiedBooksToState("data", "Fiction")
    );
    this.fetchFilteredBooksForBookbarFromServer("Juvenile Fiction").then(
      this.addVerifiedBooksToState("data", "Juvenile Fiction")
    );
    this.fetchFilteredBooksForBookbarFromServer("Education").then(
      this.addVerifiedBooksToState("data", "Education")
    );
  }
  //////////////////////////////
  //SLIDER
  //////////////////////////////
  handleSliderClick = () => {
    this.setState({
      redirect: !this.state.redirect
    });
  };
  renderRedirect = () => {
    if (this.state.redirect) {
      return <Redirect to="/books" />;
    }
  };
  //////////////////////////////
  //BOOKBAR
  //////////////////////////////
  moreFictionBooks = () => {
    if (this.state.index === this.props.fictionBooks.length - 4) {
      this.setState({
        index: 0
      });
    } else {
      this.setState({
        index: this.state.index + 4
      });
    }
  };
  //////////////////////////////
  //RENDER
  //////////////////////////////
  render() {
    let nextFour = this.props.fictionBooks.slice(
      this.state.index,
      this.state.index + 4
    );
    return (
      <div>
        <Slider handleSliderClick={this.handleSliderClick} />
        <BookBar moreBooks={this.moreFictionBooks} nextFour={nextFour} />
        {this.renderRedirect()}
      </div>
    );
  }
}

export default Home;
