import React, { Component } from "react";

// const BASEURL = "https://www.googleapis.com/books/v1/volumes";

class BookInformation extends Component {
  constructor() {
    super();
    this.state = {
      book: this.props.book
    };
  }
  render() {
    return (
      <div>
        <h1>Title: {this.props.book.title}</h1>
        <h2>Author: {this.props.book.authors}</h2>
        <h3>Publisher: {this.props.book.publisher}</h3>
        <h4>Published Date: {this.props.book.publishedDate}</h4>
        <h6>Page Count: {this.props.book.pageCount}</h6>
        <h6>Genres: {this.props.book.categories}</h6>
        <p dangerouslySetInnerHTML={{ __html: this.props.book.description }} />
      </div>
    );
  }
}

export default BookInformation;

